import Error from './Pages/ErrorNotFound'
import BookPage from './Pages/BookPage'
import UpdateBookPage from './Pages/UpdateBookPage'
import NewBook from './Pages/NewBookPage'
import DeleteBook from './Pages/DeleteBook'
import './App.css';

function App() {


