import { useState, useEffect } from 'react'
import Axios from 'axios'

const NewBook = () => {

  const [newBook, setNewBook] = useState({
    title: "",
    author: "",
    description: "",
    status: "",
    heroImage: "",
    lastUpdated: "",
    alternativeNames: [],
    tags: [],
    category: [],
    chapters: 0,
    volumes: 0,
    links: []
  })


  const createNewBook = () => {
    Axios.post("http://localhost:3000/new-book", {
      title: newBook.title,
      author: newBook.author,
      description: newBook.description,
      status: newBook.status,
      heroImage: newBook.heroImage,
      lastUpdated: newBook.lastUpdated,
      alternativeNames: newBook.alternativeNames,
      tags: newBook.tags,
      category:newBook.category,
      chapters: newBook.chapters,
      volumes:newBook.volumes,
      links: newBook.links
    }).then(response => {
      console.log(response)
    })
  }

  const handelChange = (e) => {
    const { name, value } = e.target

    setNewBook( prevBook => ({
      ...prevBook,
      [name] : value
    }))
  }


  return (
    <div className="newBook">
      <form>

        <label htmlFor="">Title</label>
        <input
          type="text"
          name="title"
          value={newBook.title}
          onChange={handelChange}
        />


        <button>Save New Book</button>
      </form>
    </div>
  )
}

export default NewBook
